import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import AvocodeLogo from "../src/images/avocode-logo.webp";


const technologies = [
    AvocodeLogo,
    AvocodeLogo,
    AvocodeLogo,
    AvocodeLogo,
    AvocodeLogo,
    AvocodeLogo,


];

const SliderComponent = () => {
  const settings = {
    infinite: true,
    speed: 1000,
    slidesToShow: 8, // Show all 8 images in desktop view
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000, // Slide every 3 seconds
    responsive: [
      {
        breakpoint: 1024, // For tablet-sized screens and smaller
        settings: {
          slidesToShow: 5,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 600, // For mobile screens
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 480, // For very small mobile screens
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <div style={{ textAlign: 'center', margin: '20px' }}>
      <h3 style={{ fontSize: '24px', marginBottom: '10px' }}>Technologies we use</h3>
      <Slider {...settings}>
        {technologies.map((image, index) => (
          <div key={index} style={{ padding: '10px', boxSizing: 'border-box' }}>
            <img 
              src={image} 
              alt={`Technology ${index + 1}`} 
              style={{ width: '100%' }} 
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default SliderComponent;
