import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import AvocodeLogo from "../../src/images/avocode-logo.webp";

const technologies = [
  AvocodeLogo,
  AvocodeLogo,
  AvocodeLogo,
  AvocodeLogo,
  AvocodeLogo,
  AvocodeLogo,
  AvocodeLogo,
  AvocodeLogo,
];

const SliderComponent = () => {
  const settings = {
    infinite: true,
    speed: 1000,
    slidesToShow: 8,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 5,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        },
      },
    ],
  };

  // Inline styles
  const wrapperStyle = {
    margin: "20px",
    width: "0%",
  };

  const titleStyle = {
    fontSize: "24px",
    marginBottom: "10px",
  };

  const sliderItemStyle = {
    padding: "10px",
    boxSizing: "border-box",
    marginRight: "50px"
  };

  const imageStyle = {
    width: "100%",
  };

  return (
    <div style={wrapperStyle}>
      <h5 style={titleStyle}>Technologies we use</h5>
      <Slider {...settings}>
        {technologies.map((image, index) => (
          <div key={index} style={sliderItemStyle}>
            <img
              src={image}
              alt={`Technology ${index + 1}`}
              style={imageStyle}
            />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default SliderComponent;
