import React, { useState } from 'react';
import "./App.css";
import Header from "../src/images/header-avocode.png";
import Main from "../src/images/main-avocode.png";
import Expertise from "../src/images/expertise-section.png";
import projectsCompletedSection from "../src/images/projects-completed-section.png";
import RecentWorks from "../src/images/recent-works.png";
import Footer from "../src/images/footer.png";

function App() {
  const [imagesLoaded, setImagesLoaded] = useState({
    header: false,
    main: false,
    expertise: false,
    projectsCompleted: false,
    recentWorks: false,
    footer: false,
  });

  const handleImageLoad = (image) => {
    setImagesLoaded(prevState => ({
      ...prevState,
      [image]: true
    }));
  };

  return (
    <div className="App">
      <img src={Header} alt="header" onLoad={() => handleImageLoad('header')} className={imagesLoaded.header ? 'loaded' : 'loading'} />
      <img src={Main} alt="main" onLoad={() => handleImageLoad('main')} className={imagesLoaded.main ? 'loaded' : 'loading'} />

      <img src={Expertise} alt="expertise" onLoad={() => handleImageLoad('expertise')} className={imagesLoaded.expertise ? 'loaded' : 'loading'} />
      <img
        src={projectsCompletedSection}
        className={`projects-completed ${imagesLoaded.projectsCompleted ? 'loaded' : 'loading'}`}
        alt="projectscompletedsection"
        onLoad={() => handleImageLoad('projectsCompleted')}
      />
      <img src={RecentWorks} alt="recentworks" onLoad={() => handleImageLoad('recentWorks')} className={imagesLoaded.recentWorks ? 'loaded' : 'loading'} />
      <img src={Footer} alt="footer" onLoad={() => handleImageLoad('footer')} className={imagesLoaded.footer ? 'loaded' : 'loading'} />
    </div>
  );
}

export default App;
